/* eslint-disable prettier/prettier */
import React, { useState, useEffect, useRef } from 'react';
import useStores from '@stores/useStores';
import { useObserver } from 'mobx-react-lite';
import { Box, Button } from '@material-ui/core';
import imgLogo from '@assets/images/img_eng_mark.png';
import TextField from '@material-ui/core/TextField';
import axios from 'axios';
// eslint-disable-next-line import/no-duplicates
import { GridView, LocalDataProvider } from 'realgrid';
// eslint-disable-next-line import/no-duplicates
import { ValueType } from 'realgrid';

const GetSearch = ({
  iconHandle,
  resetHandle,
  setBody,
  setEnd,
  size,
  setBore,
  setSpecial,
  setDisc,
  setStem,
  subclass,
  part,
  setSeat,
  setRating,
  setType,
  setPart,
  setSubclass,
  setVisible,
  visible,
}) => {
  const { commonStore } = useStores();
  const { $Dim, $setAlert } = useObserver(() => ({
    $Dim: commonStore.Dim,
    $setAlert: commonStore.fSetAlert,
  }));

  const fAlert = () => {
    $setAlert({ visible: true, desc: '행을 클릭하세요' });
    return;
  };
  const fAlert2 = () => {
    $setAlert({ visible: true, desc: '위의 정보에 해당하는 데이터가 없습니다' });
    return;
  };

  //클릭한 데이터 값 저장
  const [data, setData] = useState(null);


  useEffect(() => {
    // console.log('여기여기여기');
    subClass.current = null;
    parts.current = null;
    type.current = null;
    rating.current = null;
    seats.current = null;
    steams.current = null;
    discs.current = null;
    specials.current = null;
    bores.current = null;
    ends.current = null;
    bodys.current = null;
  }, [resetHandle]);

  useEffect(() => {
  
    if (bodys.current !== null) {
      bodys.current = null;
    } else if (ends.current !== null) {
      ends.current = null;
    } else if (bores.current !== null) {
      bores.current = null;
    } else if (rating.current !== null) {
      rating.current = null;
    
    } else if (specials.current !== null) {
      specials.current = null;
    
    } else if (discs.current !== null) {
      discs.current = null;
    } else if (steams.current !== null) {
      steams.current = null;
    } else if (seats.current !== null) {
      seats.current = null;
    } else if (rating.current !== null) {
      rating.current = null;

    } else if (type.current !== null) {
      type.current = null;
    } else if (parts.current !== null) {
      parts.current = null;
    } else if (subClass.current !== null) {
      subClass.current = null;
    
    }
  }, [iconHandle]);

  const subClass = useRef(null);
  const parts = useRef(null);
  const type = useRef(null);
  const rating = useRef(null);
  const seats = useRef(null);
  const steams = useRef(null);
  const discs = useRef(null);
  const specials = useRef(null);
  const bores = useRef(null);
  const ends = useRef(null);
  const bodys = useRef(null);

  //취소버튼 클릭 시
  const onCancel = () => {
    setVisible(false)
  };

  //테이블실행 및 조회
  const exam = async () =>{
    let result = await axios.get('/@/test/select1', {
      params: { Minor: visible.Minor, Item1: visible.Item1, Minornm: write.search },
    });
    provider.current.setRows(result.data);
    // bodys.current = result4.data;
    console.log('실행')
  }

  //확인시 
  const exam2 =() =>{
    console.log('???')
    if(visible.Minior === '062'){
      setSubclass(data);
      console.log('???2')
    }
    if(visible.Minior === '499'){
      setPart(data);
      console.log('???3')
    }

    setVisible(false)
  }

  //첫 테이블 뿌리는거
  const fCount = async () => {
    try {
      if (ends.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '063', Item1: 'BODY', Minornm: '%' },
        });
        provider.current.setRows(result4.data);
        bodys.current = result4.data;
      } else if (size !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '508', Item1: ' ', Minornm: '%' },
        });
        provider.current.setRows(result4.data);
        ends.current = result4.data;
      } else if (rating.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '506', Item1: ' ', Minornm: '%' },
        });
        provider.current.setRows(result4.data);
        bores.current = result4.data;
      } else if (specials.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '505', Item1: ' ', Minornm: '%' },
        });
        provider.current.setRows(result4.data);
        rating.current = result4.data;
      } else if (discs.current !== null) {
        let result = await axios.get('/@/test/select1', {
          params: { Minor: '504', Item1: ' ', Minornm: '%' },
        });
        provider.current.setRows(result.data);
        specials.current = result.data;
      } else if (steams.current !== null) {
        let result = await axios.get('/@/test/select1_1', {
          params: { Minor: '503', Item1: '061001', Item2: subclass.Item2, Minornm: '%' },
        });
        provider.current.setRows(result.data);
        discs.current = result.data;
      } else if (seats.current !== null) {
        let result = await axios.get('/@/test/select1_1', {
          params: { Minor: '502', Item1: '061001', Item2: subclass.Item2, Minornm: '%' },
        });
        provider.current.setRows(result.data);
        steams.current = result.data;
      } else if (part !== null ? part.Remark === 'BD' : false) {
        let result = await axios.get('/@/test/select1', {
          params: { Minor: '501', Item1: '061001', Minornm: '%' },
        });
        provider.current.setRows(result.data);
        seats.current = result.data;
      } else if (subClass.current === null && parts.current === null) {
        let result = await axios.get('/@/test/select1', {
          params: { Minor: '062', Item1: '061001', Minornm: '%' },
        });
        provider.current.setRows(result.data);
        subClass.current = result.data;
      } else if (subClass.current !== null && parts.current === null) {
        let result2 = await axios.get('/@/test/select1', {
          params: { Minor: '499', Item1: subclass.Minorcd, Minornm: '%' },
        });
        provider.current.setRows(result2.data);
        parts.current = result2.data;
        setWrite('%');
      } else if (parts.current !== null) {
        if (type.current === null) {
          let result3 = await axios.get('/@/test/select1', {
            params: { Minor: '498', Item1: part.Minorcd, Minornm: '%' },
          });
          provider.current.setRows(result3.data);
          type.current = result3.data;
          // setWrite('%');
        } else {
          let result4 = await axios.get('/@/test/select1', {
            params: { Minor: '505', Item1: ' ', Minornm: '%' },
          });
          provider.current.setRows(result4.data);
          rating.current = result4.data;
        }
      }
    } catch (error) {
      fAlert2();
      console.log(1, error);
    }
  };

  //확인버튼 클릭 시
  const onConfirm = () => {
    if (data !== null) {
      if (bodys.current !== null) {
        // console.log('요왓나?');
        setBody(data);
        setData(null);
        setVisible(false);
        // console.log('데이타',data)
      } else if (size !== null) {
        setEnd(data);
        setData(null);
        setVisible(false);
      } else if (bores.current !== null) {
        setBore(data);
        setData(null);
        setVisible(false);
      } else if (rating.current !== null) {
        setRating(data);
        setData(null);
        setVisible(false);
      } else if (specials.current !== null) {
        setSpecial(data);
        setData(null);
        setVisible(false);
      } else if (discs.current !== null) {
        setDisc(data);
        setData(null);
        setVisible(false);
      } else if (steams.current !== null) {
        setStem(data);
        setData(null);
        setVisible(false);
      } else if (part !== null ? part.Remark === 'BD' : false) {
        setSeat(data);
        setData(null);
        setVisible(false);
      } else if (rating.current !== null) {
        setRating(data);
        setData(null);
        setVisible(false);
      } else if (type.current !== null) {
        setType(data);
        setData(null);
        setVisible(false);
      } else if (parts.current !== null) {
        setPart(data);
        setData(null);
        setVisible(false);
      } else {
        setSubclass(data);
        setData(null);
        // console.log('데이타',data)
        setVisible(false);
      }
    } else {
      fAlert();
    }
  };
  // console.log('데이타2',data)
  let provider = useRef(null);
  let gridView = useRef(null);

  const [write, setWrite] = useState({
    search: '%'
  });
  const inputHandle = (e) => {
    setWrite({
      ...write,
      [e.target.name]: e.target.value,
    });
  };
console.log(write)
  //조회버튼
  const fCount2 = async () => {
    try {
      if (bodys.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '063', Item1: 'BODY', Minornm: write.search },
        });
        provider.current.setRows(result4.data);
      } else if (ends.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '508', Item1: ' ', Minornm: write.search },
        });
        provider.current.setRows(result4.data);
        // console.log(rating.current);
      } else if (bores.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '506', Item1: ' ', Minornm: write.search },
        });
        provider.current.setRows(result4.data);
        rating.current = result4.data;
        // console.log(rating.current);
      } else if (specials.current !== null) {
        let result = await axios.get('/@/test/select1', {
          params: { Minor: '504', Item1: ' ', Minornm: write.search },
        });
        provider.current.setRows(result.data);
        // console.log(rating.current);
      } else if (discs.current !== null) {
        let result = await axios.get('/@/test/select1_1', {
          params: { Minor: '503', Item1: '061001', Item2: subclass.Item2, Minornm: write.search },
        });
        provider.current.setRows(result.data);
        // console.log(rating.current);
      } else if (steams.current !== null) {
        let result = await axios.get('/@/test/select1_1', {
          params: { Minor: '502', Item1: '061001', Item2: subclass.Item2, Minornm: write.search },
        });
        provider.current.setRows(result.data);
        // console.log(rating.current);
      } else if (seats.current !== null) {
        let result = await axios.get('/@/test/select1', {
          params: { Minor: '501', Item1: '061001', Minornm: write.search },
        });
        provider.current.setRows(result.data);
        // console.log(rating.current);
      } else if (rating.current !== null) {
        let result4 = await axios.get('/@/test/select1', {
          params: { Minor: '505', Item1: ' ', Minornm: write.search },
        });
        provider.current.setRows(result4.data);
        rating.current = result4.data;
        // console.log(rating.current);
      } else if (type.current !== null) {
        let result3 = await axios.get('/@/test/select1', {
          params: { Minor: '498', Item1: part.Minorcd, Minornm: write.search },
        });
        provider.current.setRows(result3.data);
        type.current = result3.data;
        // setWrite('%');
      } else if (parts.current !== null) {
        let result2 = await axios.get('/@/test/select1', {
          params: { Minor: '499', Item1: subclass.Minorcd, Minornm: write.search },
        });

        provider.current.setRows(result2.data);
      } else if (subClass.current !== null) {
        let result1 = await axios.get('/@/test/select1', {
          params: { Minor: '062', Item1: '061001', Minornm: write.search },
        });
        provider.current.setRows(result1.data);
      }
      setWrite('%');
    } catch (error) {
      console.log(1, error);
    }
  };

  useEffect(() => {
    if (visible) {
      provider.current = new LocalDataProvider();
      gridView.current = new GridView('realgrid');
      gridView.current.setDataSource(provider.current);
      provider.current.setFields(fields);
      gridView.current.setColumns(columns);
      const height = gridView.current.displayOptions.rowHeight;
      gridView.current.displayOptions.rowHeight = height + 40;
      gridView.current.displayOptions.selectionStyle = 'rows';
      gridView.current.columnByName('Minornm').editable = false;
      gridView.current.columnByName('Remark').editable = false;
      gridView.current.sortingOptions.enabled = false;
      gridView.current.setCheckBar({
        visible: false,
      });
      gridView.current.setStateBar({
        visible: false,
      });
      registerCallback();
      exam();
      // fCount();
      // bdCount();
      // fCount2();
    }
  }, [visible]);
  function registerCallback() {
    gridView.current.onCellClicked = function (grid, clickData) {
      setData(gridView.current.getValues(clickData.itemIndex));

    };
  }

  if (!visible) return null;
  return (
    <>
      <Box style={{ position: 'fixed', zIndex: 2000, top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0, 0, 0, 0.25)' }} display="flex" justifyContent="center" alignItems="center">
        <Box style={{ height: $Dim * 400, width: $Dim * 250, background: '#fff', borderRadius: '4px', boxShadow: '0px 0px 8px rgba(0, 0, 0, 0.125)' }}>
          <Box style={{ background: '#e6e6e6', padding: $Dim * 5, borderTopLeftRadius: '4px', borderTopRightRadius: '4px' }} display="flex">
            <img src={imgLogo} alt="logo" style={{ width: $Dim * 40 }} />
          </Box>
          <Box style={{ padding: $Dim * 8 }}>
            <Box style={{ marginTop: $Dim * 10, marginBottom: $Dim * 20 }}>
              <Box style={{ height: $Dim * 300, fontSize: $Dim * 10, fontWeight: 'bold' }}>
                <Box>
                  <TextField id="outlined-basic" name="search" placeholder="검색어를 입력하세요" variant="outlined" style={{ width: $Dim * 150 }} onChange={inputHandle} />
                  <Button
                    onClick={() => {
                      exam();
                    }}
                    variant="contained"
                    style={{ marginLeft: $Dim * 15, width: $Dim * 60, height: $Dim * 30, backgroundColor: '#348fe2', fontSize: $Dim * 10, color: '#ffffff' }}
                  >
                    조회
                  </Button>
                </Box>
                <Box style={{ display: 'flex', justifyContent: 'center' }}>
                  <div id="realgrid" style={{ width: '435px', height: '500px', marginTop: 20 }} />
                </Box>
              </Box>
            </Box>{' '}
            {/* 내용부분 */}
            <Box display="flex" justifyContent="space-between">
              <Button variant="contained" style={{ flexgrow: 0.5, width: $Dim * 100, height: $Dim * 30, backgroundColor: 'red', fontSize: $Dim * 10, color: '#ffffff' }} onClick={onCancel}>
                닫기
              </Button>

              <Button variant="contained" style={{ width: $Dim * 100, height: $Dim * 30, backgroundColor: '#348fe2', fontSize: $Dim * 10, color: '#ffffff' }} onClick={exam2}>
                확인
              </Button>
            </Box>
            {/* 버튼부분 */}
          </Box>
        </Box>
      </Box>
    </>
  );
};

//그리드 컬럼및 필드
const fields = [
  {
    fieldName: 'Minornm',
    dataType: ValueType.TEXT,
  },
  {
    fieldName: 'Remark',
    dataType: ValueType.TEXT,
  },
  {
    fieldName: 'Minorcd',
    dataType: ValueType.TEXT,
  },
  {
    fieldName: 'Item1',
    dataType: ValueType.TEXT,
  },
  {
    fieldName: 'Item2',
    dataType: ValueType.TEXT,
  },
];

const columns = [
  {
    name: 'Minornm',
    fieldName: 'Minornm',
    type: 'data',
    width: '300',
    styles: {
      textAlignment: 'center',
    },
    header: {
      text: '코드명',
      showTooltip: true,
      tooltip: '<span style="color: red;">이름</span>',
    },
    renderer: {
      type: 'text',
      showTooltip: true,
    },
  },
  {
    name: 'Remark',
    fieldName: 'Remark',
    type: 'data',
    width: '100',
    styles: {
      textAlignment: 'center',
    },
    header: {
      text: '품번항목',
      showTooltip: false,
    },
    numberFormat: '0',
  },
];

export default GetSearch;
