import React, { useRef, useEffect, useState } from 'react';
import useStores from '@stores/useStores';
import { useObserver } from 'mobx-react-lite';
import { Box, Button } from '@material-ui/core';
import imgLogo from '@assets/images/img_eng_mark.png';
import TextField from '@material-ui/core/TextField';

import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles({
  table: {
    minWidth: 300,
    minheight: 300,
    marginTop: 10,
  },
});
$("example-table-1 tr").onClick(function(){
  var str = ""
  var tdArr = new Array();	// 배열 선언
  
  // 현재 클릭된 Row(<tr>)
  var tr = $(this);
  var td = tr.children();
  
  // tr.text()는 클릭된 Row 즉 tr에 있는 모든 값을 가져온다.
  console.log("클릭한 Row의 모든 데이터 : "+tr.text());
  
  // 반복문을 이용해서 배열에 값을 담아 사용할 수 도 있다.
  td.each(function(i){
    tdArr.push(td.eq(i).text());
});
console.log("배열에 담긴 값 : "+tdArr);
			
// td.eq(index)를 통해 값을 가져올 수도 있다.
var no = td.eq(0).text();
var userid = td.eq(1).text();
var name = td.eq(2).text();
var email = td.eq(3).text();


str +=	" * 클릭된 Row의 td값 = No. : <font color='red'>" + no + "</font>" +
    ", 아이디 : <font color='red'>" + userid + "</font>" +
    ", 이름 : <font color='red'>" + name + "</font>" +
    ", 이메일 : <font color='red'>" + email + "</font>";		

$("#ex1_Result1").html(" * 클릭한 Row의 모든 데이터 = " + tr.text());		
$("#ex1_Result2").html(str);
});

const GetSearch = (props) => {
  const classes = useStyles();

    const { commonStore } = useStores();
    const { $Dim } = useObserver(() => ({
      $Dim: commonStore.Dim,
    }));

    const btnRef = useRef(null);
    const [write, setWrite] = useState({
      search : "",
    });
    const inputHandle = (e) =>{
      setWrite({
        ...write,
        [e.target.name]: e.target.value,
      });
    }
    console.log(write)
    const getArrayData = props.arrayData;
    // useEffect(()=>{
    //   props.setPopupShow(true)
    // },[props])

    return (
      <>
        {console.log('여기도 들옴')}
        <Box style={{ position: 'fixed', zIndex: 2000, top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0, 0, 0, 0.25)' }} display="flex" justifyContent="center" alignItems="center">
          <Box style={{height: $Dim*300, width: $Dim * 250, background: '#fff', borderRadius: '4px', boxShadow: '0px 0px 8px rgba(0, 0, 0, 0.125)' }}>
            <Box style={{ background: '#e6e6e6', padding: $Dim * 5, borderTopLeftRadius: '4px', borderTopRightRadius: '4px' }} display="flex">
              <img src={imgLogo} alt="logo" style={{ width: $Dim * 40 }} />
            </Box>
            <Box style={{ padding: $Dim * 8 }}>
              <Box style={{ marginTop: $Dim * 10, marginBottom: $Dim * 20 }}>
              
                <Box style={{height:$Dim*211, fontSize: $Dim * 10, fontWeight: 'bold' }}>
                <Box>
                <TextField id="outlined-basic" name="search" placeholder="검색어를 입력하세요" variant="outlined" style={{width: $Dim*150}} onChange={inputHandle} />
              <Button onClick={()=>{props.searchHandle(); props.setData(write)}} variant="contained" style={{marginLeft: $Dim*15 ,width: $Dim * 60, height: $Dim * 30, backgroundColor: '#348fe2', fontSize: $Dim * 10, color: '#ffffff'}}>
                  조회
                </Button> 
                </Box>
                <div style={{display:'flex'}}>
                <TableContainer component={Paper}>
      <Table id="#example-table-1 tr" className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell style={{height:'20px'}} align="center">코드명</StyledTableCell>
            <StyledTableCell align="center">품번항목</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {getArrayData&&getArrayData.map((data) => (
            <StyledTableRow style={{height:'50px'}} key={data.Minorcd}>
              <StyledTableCell style={{height:'20px'}} align="center">{data.Minornm}</StyledTableCell>
              <StyledTableCell style={{height:'20px'}} align="center">{data.Minornm.slice(0,1)}</StyledTableCell>
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
                  </div>
                {/* 테이블 들어갈 자리 */}
                </Box>
              </Box> {/* 내용부분 */}
              <Box display="flex" justifyContent="space-between">
                <Button variant="contained" style={{flexgrow:0.5 ,width: $Dim * 100, height: $Dim * 30, backgroundColor: 'red', fontSize: $Dim * 10, color: '#ffffff'}} onClick={()=>props.setPopupShow(false)}>
                 닫기
                </Button>
                <Button variant="contained" style={{ width: $Dim * 100, height: $Dim * 30, backgroundColor: '#348fe2', fontSize: $Dim * 10, color: '#ffffff' }} onClick={()=>props.popupHandle2()}>
                확인
                </Button>
                
              </Box>{/* 버튼부분 */}
            </Box>
          </Box>
        </Box>
      </>
    );
};

export default GetSearch;
