import React,{useRef,useEffect} from 'react';
import Modal from '@material-ui/core/Modal';
import { makeStyles } from '@material-ui/core/styles';
import { useObserver } from 'mobx-react-lite';
import useStores from '@stores/useStores';


function rand() {
    return Math.round(Math.random() * 20) - 10;
  }
  
  function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();
  
    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: `translate(-${top}%, -${left}%)`,
    };
  }
  
  const useStyles = makeStyles((theme) => ({
    paper: {
      position: 'absolute',
      width: 400,
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));

const Modals = ({ visible, description, confirmText = '확인', onConfirm }) => {
    const { commonStore } = useStores();
    const {$Dim,$setAlert} = useObserver(() => ({
        $Dim: commonStore.Dim,
        $setAlert: commonStore.fSetAlert,
        // $Confirm: commonStore.Confirm,
        // $setConfirm: commonStore.fSetConfirm,
        // $setConfirmFunc: commonStore.fSetConfirmFunc,
      }));
      const Styles = fStyles($Dim);



    const classes = useStyles();
  // getModalStyle is not a pure function, we roll the style only on the first render
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div style={modalStyle} className={classes.paper}>
      <h2 id="simple-modal-title">Text in a modal</h2>
      <p id="simple-modal-description">
        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
      <button onClick={handleClose}>확인</button><button>닫기</button>
      {/* <Modals /> */}
    </div>
  );


  const btnRef = useRef(null);

  useEffect(() => {
    if (visible) {
      btnRef.current.focus();
    }
  }, [visible]);

  if (!visible) return null;  


    return (
        <div>
     <button type="button" onClick={handleOpen}>
        Open Modal
      </button>
      <Modal
        open={open}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
        </div>
    );
};

const fStyles = ($Dim) => {
    return {
      s1: { width: $Dim * 1000, height: $Dim * 550, background: '#fff' },
    };
  };

export default Modals;
