import React, { useEffect, useState } from 'react';
import { useObserver } from 'mobx-react-lite';
import { withRouter } from 'react-router-dom';
import { Box,Button } from '@material-ui/core'; //Button
import useStores from '@stores/useStores';
import FormControl from '@material-ui/core/FormControl';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import axios from 'axios';

const Lookup = () => {

    const { commonStore } = useStores();
    const {$Dim,$setAlert,$setaaSearchFunc,$GetSearch, $setGetSearch, $setGetSearchFunc, $Confirm, $setConfirm, $setConfirmFunc } = useObserver(() => ({
        $Dim: commonStore.Dim,
        $Confirm: commonStore.Confirm,
        $setConfirm: commonStore.fSetConfirm,
        $setConfirmFunc: commonStore.fSetConfirmFunc,
        $GetSearch: commonStore.GetSearch,
        $setGetSearch: commonStore.fSetGetSearch,
        $setGetSearchFunc: commonStore.fSetGetSearchFunc,
        $setaaSearchFunc: commonStore.fSetaaSearchFunc,
      }));
      const Styles = fStyles($Dim);

      const [soView, setSoView] =useState(false);
      const soViewHandle = () =>{
        setSoView(!soView);
      }

      const [aaa,setAaa] = useState('z');
      


      const fConfirm = () => {
        $setGetSearch({
          visible: true,
          desc: '납품자료를 저장하시겠습니까?',
          id: 'save',
        });
      };


    
      const fConfirmFunc = async () => {
        //확인 눌렀을 떄  $setGetSearchFunc
        $setGetSearchFunc(async () => {
          $setGetSearch({ visible: false, desc: "", id: 'save' });
          if ($GetSearch.id === 'save') {
            soViewHandle();
            console.log('저장완료');
          }
        });
        $setaaSearchFunc(async ()=>{
          console.log('조회버튼 성공')
          let result =  await axios.get('/@/test/select1',{
            params: { Minor: 'G' },
          })
          setAaa(result.data[0].Minornm)
          console.log(result.data[0].Minornm);
        })
      };


      useEffect(()=>{
        console.log(1,'여기들어옴')
        fConfirmFunc();
       
      },[$GetSearch])

    return (
        <>
        <Box  style={Styles.s1} display="flex" flexDirection="column" alignItems="center">
          <Box style={{height: $Dim * 30 }}></Box>
            <Box  style={{ width: $Dim * 500, height: $Dim * 20, marginTop: $Dim * 20  }} display="flex" flexDirection="column" alignItems="center">
                <Box style={{display:'flex', justifyContent:'space-around'}}>
                <Button onClick={fConfirm} variant="contained" style={{marginRight:$Dim * 5}}>초기화</Button>
                <Button variant="contained" style={{marginRight:$Dim * 5}}>품번확인</Button>
                </Box>
            </Box>
        
            <Box  style={{ width: $Dim * 500, height: $Dim * 200, marginTop: $Dim * 20  }} display="flex" flexDirection="column" alignItems="center">
              <Box style={{display:'flex',fontSize:$Dim*10, alignItems:"center"}}>
           <div>
             소분류 :
          <FormControl onClick={fConfirm} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount">{aaa}</InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          {soView && 
          <FormControl onClick={fConfirm} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>}
          </div>  
            </Box>
            </Box> 
        </Box>
        </>
    );
};

const fStyles = ($Dim) => {
    return {
      s1: { width: $Dim * 1000, height: $Dim * 550, background: '#fff' },
      s2: { width: $Dim * 450, height: $Dim * 250,marginBottom: $Dim * 150, marginTop: $Dim * 220},
    };
  };

export default withRouter(Lookup);
