import React, { useRef, useEffect } from 'react';
import useStores from '@stores/useStores';
import { useObserver } from 'mobx-react-lite';
import { Box, Button } from '@material-ui/core';
import imgLogo from '@assets/images/img_eng_mark.png';
import TextField from '@material-ui/core/TextField';


const GetSearch = ({ visible, description, confirmText = '확인', cancelText = '취소', onConfirm, onCancel }) => {

    const { commonStore } = useStores();
    const { $Dim } = useObserver(() => ({
      $Dim: commonStore.Dim,
    }));

    const btnRef = useRef(null);
  
    useEffect(() => {
      if (visible) {
        btnRef.current.focus();
      }
    }, [visible]);
  
    if (!visible) return null;

    return (
        <>
        {console.log('여기도 들옴')}
        <Box style={{ position: 'fixed', zIndex: 2000, top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0, 0, 0, 0.25)' }} display="flex" justifyContent="center" alignItems="center">
          <Box style={{height: $Dim*300, width: $Dim * 250, background: '#fff', borderRadius: '4px', boxShadow: '0px 0px 8px rgba(0, 0, 0, 0.125)' }}>
            <Box style={{ background: '#e6e6e6', padding: $Dim * 5, borderTopLeftRadius: '4px', borderTopRightRadius: '4px' }} display="flex">
              <img src={imgLogo} alt="logo" style={{ width: $Dim * 40 }} />
            </Box>
            <Box style={{ padding: $Dim * 8 }}>
              <Box style={{ marginTop: $Dim * 10, marginBottom: $Dim * 20 }}>
              
                <Box style={{display:'flex',height:$Dim*211, fontSize: $Dim * 10, fontWeight: 'bold' }}>
                <Box>
                <TextField id="outlined-basic" placeholder="검색어를 입력하세요" variant="outlined" style={{width: $Dim*150}}  />
              <Button onClick={onSelect} variant="contained" style={{marginLeft: $Dim*15 ,width: $Dim * 60, height: $Dim * 30, backgroundColor: '#348fe2', fontSize: $Dim * 10, color: '#ffffff'}}>
                  조회
                </Button> 
                </Box>
                {/* 테이블 들어갈 자리 */}

    {/*  */}

    {/*  */}

                </Box>
              </Box> {/* 내용부분 */}

              <Box display="flex" justifyContent="space-between">
                <Button variant="contained" style={{flexgrow:0.5 ,width: $Dim * 100, height: $Dim * 30, backgroundColor: 'red', fontSize: $Dim * 10, color: '#ffffff'}} onClick={onCancel} ref={btnRef}>
                {cancelText}
                </Button>
                
                <Button variant="contained" style={{ width: $Dim * 100, height: $Dim * 30, backgroundColor: '#348fe2', fontSize: $Dim * 10, color: '#ffffff' }} onClick={onConfirm}>
                {confirmText}
                </Button>
                
              </Box>{/* 버튼부분 */}
            </Box>
          </Box>
        </Box>
      </>
    );
};

export default GetSearch;
