import React,{useRef,useState,useEffect} from 'react';
import { Box,Button } from '@material-ui/core'; //Button
import useStores from '@stores/useStores';
import { useObserver } from 'mobx-react-lite';
import { withRouter } from 'react-router-dom';
import { TreeView, LocalTreeDataProvider } from 'realgrid';
import { columns, fields } from './realgrid-data';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';

const Start = () => {

    const { commonStore } = useStores();
    const {$Dim,$setAlert} = useObserver(() => ({
        $Dim: commonStore.Dim,
        $setAlert: commonStore.fSetAlert,
        // $Confirm: commonStore.Confirm,
        // $setConfirm: commonStore.fSetConfirm,
        // $setConfirmFunc: commonStore.fSetConfirmFunc,
      }));
      const[update, setUpdate] = useState([]);
      const Styles = fStyles($Dim);
      const [modify, setModify] = useState(false);
      const modifyHandle = () =>{
        setDateAdd(false)
        setModify(!modify)
        let focusCell = treeView.current.getCurrent();
        // console.log(focusCell.dataRow)
        console.log(1,treeView.current.getValues(2))
        setUpdate(treeView.current.getValues(focusCell.dataRow))
        
        
      }
      const [dateAdd, setDateAdd] = useState(false);
      const addHandle = () =>{
        setModify(false)
        setDateAdd(!dateAdd)
      }
      const fCount = async () => {
        try {
          let result = await axios.get('/@/test/aaa');
          treeProvider.current.setRows(result.data,'Minorcd',false,'','');

        } catch (error) {
          if (error.response.status === 404) {
            $setAlert({ visible: true, desc: '해당 코드가 없습니다.' });
          } else {
            $setAlert({ visible: true, desc: '조회중 오류가 발생하였습니다.' });
          }
        }
      };

      let treeProvider  = useRef(null);
      let treeView  = useRef(null);
    //   const [format,setFormat]= useState(false); //값이 변경되었을 때 화면을 다시 그리기 위함

      useEffect(()=>{
        (treeView.current === null) && (treeProvider.current = new LocalTreeDataProvider());
        (treeView.current === null) && (treeView.current = new TreeView("realgrid"));
        treeView.current.setDataSource(treeProvider.current);
        treeProvider.current.setFields(fields); 
        treeView.current.setColumns(columns);
        //로우 전체 선택
        treeView.current.displayOptions.selectionStyle = "rows"

        treeView.current.setRowStyleCallback(function(grid, item, fixed) {
          // var ret = {};
          var Minorcd = grid.getValue(item.index, "Minorcd");
          console.log(2,Minorcd.length)
          if (Minorcd.length ===3) {
            return "yellow-color";
          }
        })

        

        treeView.current.columnByName("Minor").editable = false;
        treeView.current.columnByName("Minorcd").editable = false;
        treeView.current.columnByName("Minornm").editable = false;
        treeView.current.columnByName("SortSeq").editable = false;
        treeView.current.columnByName("Level").editable = false;
        treeView.current.columnByName("Remark").editable = false;
        treeView.current.setCheckBar({
            visible: false
          });
          treeView.current.setEditOptions({
          insertable: true,
          appendable: true,
          updatable: true,
          deletable: true, //CRUD 하기 위한 작업들
          softDeleting: true,
        });
        console.log("여기 들옴")

        treeView.current.cancel(); //수정하고 저장할때 오류를 막기 위함
        fCount();

        
      },[])


     
      


    return (
        <>  
      <Box style={Styles.s1} display="flex" flexDirection="column" alignItems="center">
        <Box style={{ width: $Dim * 500, height: $Dim * 200, marginTop: $Dim * 20  }} display="flex" flexDirection="column" alignItems="center" justifyContent="center">
        <div id="realgrid" style={Styles.s2}></div>
        </Box>


      
        <Box style={{ width: $Dim * 700, height: $Dim * 200 ,backgroundColor:'red',marginTop: $Dim*70 }}  justifyContent="space-between">
        <div style={{display:'flex' , justifyContent:'space-between'}}>
        <div>
        <Button variant="contained" onClick={modifyHandle}  style={{marginRight:$Dim * 5}}>신규항목</Button>
        <Button variant="contained" onClick={addHandle}>코드추가</Button>
        </div>
        <div>
        <Button variant="contained" style={{marginRight:$Dim * 5}}>삭제</Button>
        <Button variant="contained">저장</Button>
        </div>
        </div>
        <Box style={{ width: $Dim * 680, height: $Dim * 160 ,backgroundColor:'yellow',marginLeft: $Dim*10,marginTop: $Dim*10 }}  justifyContent="space-between">
        
        {modify &&<TextField label="MinorCd" id="filled-size-small" defaultValue="{update.Minorcd}" variant="filled" size="small"/>}
        {modify &&<TextField label="Minor" id="filled-size-small" defaultValue="{update.Minorcd}" variant="filled" size="small"/>}
        {modify &&<TextField label="MinorNm" id="filled-size-small" defaultValue="{update.Minorcd}" variant="filled" size="small"/>}
        {modify &&<TextField label="CreDate" id="filled-size-small" defaultValue="{update.Minorcd}" variant="filled" size="small"/>}

        {dateAdd &&<TextField label="zzzz" id="filled-size-small" defaultValue="" variant="filled" size="small"/>}
        {dateAdd &&<TextField label="Minor" id="filled-size-small" defaultValue="" variant="filled" size="small"/>}
        {dateAdd &&<TextField label="MinorNm" id="filled-size-small" defaultValue="" variant="filled" size="small"/>}
        {dateAdd &&<TextField label="CreDate" id="filled-size-small" defaultValue="" variant="filled" size="small"/>}

        </Box>
        </Box>

      </Box>
      
        </> 
    );
};

const fStyles = ($Dim) => {
    return {
      s1: { width: $Dim * 1000, height: $Dim * 550, background: '#fff' },
      s2: { width: $Dim * 700, height: $Dim * 250,marginBottom: $Dim * 150, marginTop: $Dim * 220},
    };
  };

export default withRouter(Start);
