import React, { useEffect, useState } from 'react';
import { useObserver } from 'mobx-react-lite';
import { withRouter } from 'react-router-dom';
import { Box,Button } from '@material-ui/core'; //Button
import useStores from '@stores/useStores';
import FormControl from '@material-ui/core/FormControl';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import GetSearch from 'components/common/GetSearch';
import axios from 'axios';

const Lookup = (props) => {

    const { commonStore } = useStores();
    const {$Dim,$setAlert} = useObserver(() => ({
        $Dim: commonStore.Dim,
        $Confirm: commonStore.Confirm,
        $setConfirm: commonStore.fSetConfirm,
        $setConfirmFunc: commonStore.fSetConfirmFunc,
      }));
      const Styles = fStyles($Dim);

      //줄 추가 기능
      const [lineShow, setLineShow] =useState(false);
      const lineHandle =() =>{
        setLineShow(!lineShow);
      }
      const [lineShow2, setLineShow2] =useState(false);
      const lineHandle2 =() =>{
        lineShow && setLineShow(true)
        setLineShow2(!lineShow2);
      }      
      const [lineShow3, setLineShow3] =useState(false);
      const lineHandle3 =() =>{
        lineShow && setLineShow(true)
        lineShow2 && setLineShow2(true)
        setLineShow3(!lineShow3);
      }    
      const [lineShow4, setLineShow4] =useState(false);
      const lineHandle4 =() =>{
        setLineShow4(!lineShow4);
      }    
      const [lineShow5, setLineShow5] =useState(false);
      const lineHandle5 =() =>{
        setLineShow5(!lineShow5);
      }    
      const [lineShow6, setLineShow6] =useState(false);
      const lineHandle6 =() =>{
        setLineShow6(!lineShow6);
      }    
      const [lineShow7, setLineShow7] =useState(false);
      const lineHandle7 =() =>{
        setLineShow7(!lineShow7);
      }    

      //팝업 닫기 기능
      const [popupShow, setPopupShow] =useState(false);
      const popupHandle =() =>{
        setPopupShow(!popupShow);
      }


      //조회 기능
      // const[search, setSearch] = useState(false);
      const searchHandle =() =>{
        
        console.log('써치 핸들 들어옴')
        console.log('써치 핸들 들어오고 쓴 데이터 값',data)
        if(!lineShow2){
          fConfirmFunc();
          console.log(lineShow2)
        }else if(lineShow2){
          fConfirmFunc();
          console.log(lineShow2)
        }
        

      }

      //팝업창 확인 기능
      const popupHandle2 =() =>{
        setPopupShow(!popupShow);
        lineHandle();
        lineShow &&  lineHandle2();
        lineShow2 && lineHandle3();
        // !lineShow2 && !lineShow && lineHandle3();
        // !lineShow3 && !lineShow2 && !lineShow && lineHandle4();
        // !lineShow4 && !lineShow3 && !lineShow2 && !lineShow && lineHandle5();
        // !lineShow5 && !lineShow4 && !lineShow3 && !lineShow2 && !lineShow &&  lineHandle6();
        // !lineShow6 && !lineShow5 && !lineShow4 && !lineShow3 && !lineShow2 && !lineShow && lineHandle7();
      }

      //data 받기 
      const[data, setData] = useState("");
      const [arrayData, setArrayData] = useState();


      const fConfirmFunc = async () => {
      
        
        console.log('aaa')
        console.log(1,data)
        if(data !== ""){

          let result = await axios.get('/@/test/select1', {
              params: { Minor: '062', Item1: '061001',Minornm: data.search },
            });
          console.log('와우',result.data);
          setArrayData(result.data)
      }
      }

      const fConfirmFunc2 = async () => {
      
        console.log('aaa')
        console.log(1,data)
        if(arrayData !== null){

          let result = await axios.get('/@/test/select1', {
              params: { Minor: '499', Item1:arrayData.Minorcd,Minornm: data.search  },
            });
          console.log('와우',result.data);
          setArrayData(result.data)
      }
      }
      useEffect(()=>{

      },[])
      console.log(arrayData)

// console.log(1,data)
    return (
        <>
        {popupShow&&<GetSearch setPopupShow={setPopupShow} searchHandle={searchHandle} setData={setData} arrayData={arrayData} popupHandle2={popupHandle2}/> }
        <Box  style={Styles.s1} display="flex" flexDirection="column" alignItems="center">
          <Box style={{height: $Dim * 30 }}></Box>
            <Box  style={{ width: $Dim * 500, height: $Dim * 20, marginTop: $Dim * 20  }} display="flex" flexDirection="column" alignItems="center">
                <Box style={{display:'flex', justifyContent:'space-around'}}>
                <Button  variant="contained" style={{marginRight:$Dim * 5}}>초기화</Button>
                <Button variant="contained" style={{marginRight:$Dim * 5}}>품번확인</Button>
                </Box>
            </Box>
        
            <Box  style={{ width: $Dim * 500, height: $Dim * 200, marginTop: $Dim * 20  }} display="flex" flexDirection="column" alignItems="center">
              <Box style={{display:'flex',fontSize:$Dim*10, alignItems:"center"}}>
           <div>
             <div>소분류 :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          {lineShow && 
          <div>
          <div>반제품 PART :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>}
          {lineShow &&lineShow2 && 
          <div>
          <div>반제품 TYPE :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>}
          {lineShow &&lineShow2 &&lineShow3 && 
          <div>
          <div>SEAT :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>}
          {/* {lineShow4 && 
          <div>
          <div>반제품 PART :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>}
          {lineShow5 && 
          <div>
          <div>반제품 PART :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>}
          {lineShow6 && 
          <div>
          <div>반제품 PART :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>}
          {lineShow7 && 
          <div>
          <div>반제품 PART :</div>
          <FormControl onClick={popupHandle} fullWidth variant="outlined" style={{width:$Dim*400}}>
            <InputLabel htmlFor="outlined-adornment-amount"></InputLabel>
            <OutlinedInput id="outlined-adornment-amount" labelWidth={60}/>
          </FormControl>
          </div>} */}
          </div>  
            </Box>
            </Box> 
        </Box>
        </>
    );
};

const fStyles = ($Dim) => {
    return {
      s1: { width: $Dim * 1000, height: $Dim * 550, background: '#fff' },
      s2: { width: $Dim * 450, height: $Dim * 250,marginBottom: $Dim * 150, marginTop: $Dim * 220},
    };
  };

export default withRouter(Lookup);
