import { decorate, observable, action } from 'mobx';

class CommonStore {
  constructor() {
    this.Dim = window.innerWidth / 1000;
    this.Loading = false;
    this.Alert = { visible: false, desc: '' };
    this.Confirm = { visible: false, desc: '', id: '' };
    this.ConfirmFunc = new (function () {})();
    this.Title = '';
    this.Modals = {visible: false, desc: ''};
  }

  fSetDim = (info) => {
    this.Dim = info;
  };

  fSetLoading = (state) => {
    this.Loading = state;
  };

  fSetAlert = (info) => {
    this.Alert = info;
  };

  fSetConfirm = (info) => {
    this.Confirm = info;
  };

  fSetConfirmFunc = (info) => {
    this.ConfirmFunc = info;
  };

  fSetTitle = (info) => {
    this.Title = info;
  };

  fSetModal = (info) =>{
    this.Modal = info;
  }
}

decorate(CommonStore, {
  Dim: observable,
  Loading: observable,
  Alert: observable,
  Confirm: observable,
  ConfirmFunc: observable,
  Title: observable,
  Modal: observable,
  fSetDim: action,
  fSetState: action,
  fSetAlert: action,
  fSetConfirm: action,
  fSetConfirmFunc: action,
  fSetTitle: action,
  fSetModal:action,
});

export default CommonStore;
