import React from 'react';
import { Box } from '@material-ui/core'; //Button
import useStores from '@stores/useStores';
import { useObserver } from 'mobx-react-lite';
import { withRouter } from 'react-router-dom';
import { GridView, LocalDataProvider } from 'realgrid';
import { columns, fields } from './realgrid-data';

const Start = () => {



    const { commonStore } = useStores();
    const {$Dim} = useObserver(() => ({
        $Dim: commonStore.Dim,
        // $setAlert: commonStore.fSetAlert,
        // $Confirm: commonStore.Confirm,
        // $setConfirm: commonStore.fSetConfirm,
        // $setConfirmFunc: commonStore.fSetConfirmFunc,
      }));

      const Styles = fStyles($Dim);


      let provider = useRef(null);
      let gridView = useRef(null);
      const [format,setFormat]= useState(false); //값이 변경되었을 때 화면을 다시 그리기 위함
    
      useEffect(()=>{
        (gridView.current === null) && (provider.current = new LocalDataProvider());
        (gridView.current === null) && (gridView.current = new GridView("realgrid"));
        gridView.current.setDataSource(provider.current);
        provider.current.setFields(fields); 
        gridView.current.setColumns(columns);
        gridView.current.setEditOptions({
          insertable: true,
          appendable: true,
          updatable: true,
          deletable: true, //CRUD 하기 위한 작업들
          softDeleting: true,
        });
        setFormat(false);
        console.log("여기 들옴")
        gridView.current.cancel(); //수정하고 저장할때 오류를 막기 위함
        axios.get('/api/hansun') //테이블데이터 표현
        .then(res => {
          console.log('함보자',res.data)
          provider.current.setRows(res.data);
        });
      },[format])
      

    return (
        <>  
      <Box style={Styles.s1} display="flex" flexDirection="column" alignItems="center">
        <Box style={{ width: $Dim * 500, height: $Dim * 200, marginTop: $Dim * 20 }} display="flex" flexDirection="column" alignItems="center" justifyContent="center">
        <div id="realgrid" style={{width: "1600px"}}></div>
          {/* <Button variant="contained" style={{ width: $Dim * 300, height: $Dim * 70, fontSize: $Dim * 25, backgroundColor: '#323232', color: '#ffffff' }}>
            Alert
          </Button>
          <Button variant="contained" style={{ width: $Dim * 300, height: $Dim * 70, marginTop: $Dim * 10, fontSize: $Dim * 25, backgroundColor: '#323232', color: '#ffffff' }}>
            Confirm
          </Button>
          <Button variant="contained" style={{ width: $Dim * 300, height: $Dim * 70, marginTop: $Dim * 10, fontSize: $Dim * 25, backgroundColor: '#323232', color: '#ffffff' }}>
            DATA
          </Button> */}
        </Box>
      </Box>
        </> 
    );
};

const fStyles = ($Dim) => {
    return {
      s1: { width: $Dim * 1000, height: $Dim * 550, background: '#fff' },
      s2: { width: $Dim * 500, height: $Dim * 450 },
    };
  };

export default withRouter(Start);
