import React, { useState, useEffect, useRef } from 'react';
import useStores from '@stores/useStores';
import { useObserver } from 'mobx-react-lite';
import { Box, Button } from '@material-ui/core';
import imgLogo from '@assets/images/img_eng_mark.png';
import { GridView, LocalDataProvider, ValueType } from 'realgrid';
import axios from 'axios';

const FinalSelect = ({ size,rating,stem,seat,type,part,stgrade,subclass,visible4, setVisible4 }) => {
  const { commonStore } = useStores();
  const { $Dim, $setAlert } = useObserver(() => ({
    $Dim: commonStore.Dim,
    $setAlert: commonStore.fSetAlert,
  }));

  const onCancel = () => {
    setVisible4(false);
    // setSubclass('');
  };
  let provider = useRef(null);
  let gridView = useRef(null);

  const fCount = async () => {
    try {
      let result = await axios.get('/@/test/finalSelect', {
        params: {
          subclass: subclass.Minorcd,
          stgrade: stgrade.Minorcd,
          part: part.Minorcd,
          type: type.Minorcd,
          seat: '',
          stem: '',
          disc: '',
          special: '',
          rating: rating.Minorcd,
          bore: '',
          size: size.Minorcd,
          end: '',
          body: '',
        },
      });
      provider.current.setRows(result.data);
    } catch (error) {
      console.log(1, error);
    }
  };

  useEffect(() => {
    if (visible4) {
      provider.current = new LocalDataProvider();
      gridView.current = new GridView('realgrid');
      gridView.current.setDataSource(provider.current);
      provider.current.setFields(fields);
      gridView.current.setColumns(columns);
      const height = gridView.current.displayOptions.rowHeight;
      gridView.current.displayOptions.rowHeight = height + 40;
      gridView.current.displayOptions.selectionStyle = 'rows';
      gridView.current.columnByName('goodnm').editable = false;
      gridView.current.columnByName('goodno').editable = false;
      gridView.current.columnByName('spec').editable = false;
      gridView.current.setCheckBar({
        visible: false,
      });
      gridView.current.setStateBar({
        visible: false,
      });
      fCount();
    }
  }, [visible4]);
  console.log('여기여기여기1111', visible4);
  if (!visible4) return null;
  return (
    <>
      <Box style={{ position: 'fixed', zIndex: 2000, top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0, 0, 0, 0.25)' }} display="flex" justifyContent="center" alignItems="center">
        <Box style={{ height: $Dim * 200, width: $Dim * 250, background: '#fff', borderRadius: '4px', boxShadow: '0px 0px 8px rgba(0, 0, 0, 0.125)' }}>
          <Box style={{ background: '#e6e6e6', padding: $Dim * 5, borderTopLeftRadius: '4px', borderTopRightRadius: '4px' }} display="flex">
            <img src={imgLogo} alt="logo" style={{ width: $Dim * 40 }} />
          </Box>
          <Box style={{ padding: $Dim * 8 }}>
            <Box style={{ marginTop: $Dim * 10, marginBottom: $Dim * 5 }}>
              <Box style={{ height: $Dim * 100, fontSize: $Dim * 10, fontWeight: 'bold' }}>
                <Box style={{ display: 'flex', justifyContent: 'center' }}>
                  <div id="realgrid" style={{ width: '435px', height: '200px' }} />
                </Box>
              </Box>
            </Box>{' '}
            {/* 내용부분 */}
            <Box display="flex" justifyContent="center">
              <Button variant="contained" style={{ flexgrow: 0.5, width: $Dim * 100, height: $Dim * 30, backgroundColor: 'red', fontSize: $Dim * 10, color: '#ffffff' }} onClick={onCancel}>
                닫기
              </Button>
            </Box>
            {/* 버튼부분 */}
          </Box>
        </Box>
      </Box>
    </>
  );
};

const fields = [
  {
    fieldName: 'goodnm',
    dataType: ValueType.TEXT,
  },
  {
    fieldName: 'goodno',
    dataType: ValueType.TEXT,
  },
  {
    fieldName: 'spec',
    dataType: ValueType.TEXT,
  },
];

const columns = [
  {
    name: 'goodnm',
    fieldName: 'goodnm',
    type: 'data',
    width: '133',
    styles: {
      textAlignment: 'center',
    },
    header: {
      text: 'goodnm',
      showTooltip: true,
      tooltip: '<span style="color: red;">이름</span>',
    },
    renderer: {
      type: 'text',
      showTooltip: true,
    },
  },
  {
    name: 'goodno',
    fieldName: 'goodno',
    type: 'data',
    width: '133',
    styles: {
      textAlignment: 'center',
    },
    header: {
      text: 'goodno',
      showTooltip: false,
    },
    numberFormat: '0',
  },
  {
    name: 'spec',
    fieldName: 'spec',
    type: 'data',
    width: '133',
    styles: {
      textAlignment: 'center',
    },
    header: {
      text: 'spec',
      showTooltip: false,
    },
    numberFormat: '0',
  },
];

export default FinalSelect;
